// Load font file and convert to base64
export const MorbitroneFontBase64 =
  ""; // Your base64 encoded font data

export function injectFontStyles(html: string): string {
  return html.replace(
    /@font-face\s*{[^}]*}/g,
    `@font-face {
      font-family: 'Morbitrone';
      src: url(data:application/x-font-woff;charset=utf-8;base64,${MorbitroneFontBase64}) format('woff');
      font-weight: normal;
      font-style: normal;
    }`
  );
}
